$(function(){$("#menu a").click(function(){var element=$(this).attr('href');if(element=='#feature'){$('html, body').animate({scrollTop:500},2000)}else{$('html, body').animate({scrollTop:$(element).offset().top},2000)}});$('.slider').slick({infinite:!0,dots:!0,arrows:!0,});$('.review').slick({infinite:!0,dots:!1,arrows:!0,});$(window).scroll(function(){if($(this).scrollTop()>=50){$('#return-to-top').fadeIn(200)}else{$('#return-to-top').fadeOut(200)}});$('#return-to-top').click(function(){$('body,html').animate({scrollTop:0},500)})})